(async() => {

    const browser = await puppeteer.launch({args: ['--no-sandbox', '--disable-setuid-sandbox', '--shm-size=1gb', '--disable-dev-shm-usage']});
    let page = await browser.newPage();

    try{

        log('Setting Session Cookies');
        await page.setCookie(...msg.session.cookies);

        await page.on('response', response => {
             if (response.url().includes(msg.page.listenTo[0]) && response.status()===200){

                //msg.headers = response.request().headers();
                //msg.postData = response.request().postData();

                response.json().then(function (json) {

                    msg.topic = 'RESPONSE_FROM_' + response.url();
                    msg.payload = json;

                    node.send(msg);

                    log(msg.page.website.name + ' -- Sending a ' + msg.topic + ' message');

                    log(msg.page.website.name + ' -- Finish Closing browser...');

                    browser.close();

                    log(msg.page.website.name + ' -- Goodbye!!');
                })

            }
        });

        log('Go to page ' + msg.page.url);
        await page.goto(msg.page.url);

        log(msg.page.website.name + ' -- Wait For Selector ' + msg.page.waitForSelector + ' ...')
        await page.waitForSelector(msg.page.waitForSelector,{timeout : 10000});
        //await page.waitForNavigation({waitUntil: 'load'});

        const url = await page.evaluate(() => location.href);

        await msg.session.history.push({
            url: url,
            timestamp: Date.now()
        });

        log(msg.page.website.name + ' -- Waiting ' + (msg.page.waitFor/1000) + ' seconds for responses before closing browser');
        await sleep(msg.page.waitFor);

        log(msg.page.website.name + ' -- Timeout Closing browser...');

        await page.close();
        await browser.close();

        log(msg.page.website.name + ' -- Goodbye!!');
    }
    catch(e){
        node.error(e);

        log(msg.page.website.name + ' -- Closing browser...');

        await browser.close();

        log(msg.page.website.name + ' -- Goodbye!!');

        await page.close();
        await browser.close();

        //return msg;
    }
})();

