Date.prototype.getRoundHours = function() {
  var mm = this.getMonth() + 1; // getMonth() is zero-based
  var dd = this.getDate();
  var hh = this.getHours();

  return [this.getFullYear(),
          (mm>9 ? '' : '0') + mm + '-',
          (dd>9 ? '' : '0') + dd + ' ',
          (hh>9 ? '' : '0') + hh + ':00:00',
         ].join('');
};

