'use strict'; 
msg.topic = "1betpro";

const puppeteer = global.get('puppeteer');

(async() => {
    const browser = await puppeteer.launch({args: msg.payload.value.browser_args});
    const page = await browser.newPage();

    await page.goto(msg.payload.value.url);

    await page.type(msg.payload.value.username_selector, msg.payload.value.username);
    await page.type(msg.payload.value.password_selector, msg.payload.value.password);

    await page.waitForSelector(msg.payload.value.login_button_selector);

    await page.click(msg.payload.value.login_button_selector);

    await page.waitForSelector(msg.payload.value.logout_user_selector);

    await page.goto(msg.payload.value.next_url);

    await page.waitForSelector(msg.payload.value.wait_for_selector);

    // Extract the results from the page.
    rows = await page.evaluate(results_selector => {

        //Extract
        anchors = Array.from(document.querySelectorAll(results_selector));


        //Transform
        return anchors.map((anchor, index, array) => {
            row = {};

            row.fixed_number_of_total_td_in_tr = 10;
            row.array_length = array.length;

            row.index = index;
            row.event_number = Math.floor(row.index/10);
            row.textContent = `${anchor.textContent}`;
            row.innerHTML = `${anchor.innerHTML}`;

            if(`${anchor.id}`!==""){
                row.id = `${anchor.id}`;

                row.team_anchor_index = (row.event_number*10);

                //team_anchor = array[team_anchor_index];

                //console.log(team_anchor);

                //row.team_innerHTML = `${team_anchor.innerHTML}`;

                row.source = anchor;

                row.elements = row.innerHTML.split("</");
                //custom transformation
                row.id_parts = row.id.split("_");
                row.part0 = (row.id_parts.length > 0) ? row.id_parts[0] : null;
                row.part1 = (row.id_parts.length > 1) ? row.id_parts[1] : null;
                row.part2 = (row.id_parts.length > 2) ? row.id_parts[2] : null;
                row.part3 = (row.id_parts.length > 3) ? row.id_parts[3] : null;
                row.part4 = (row.id_parts.length > 4) ? row.id_parts[4] : null;

                //row.team  = (team_innerHTML.lenght>0) ? team_innerHTML.split("</")[1].split(">")[team_innerHTML.split("</")[1].split(">").length-1] : null;
                //row.score = (team_innerHTML.lenght>0) ? team_innerHTML.split("</")[0].split(">")[team_innerHTML.split("</")[0].split(">").length-1] : null;

                row.bet_1x2 = (row.part2==="02")? row.textContent : null;

                row.bet_handicap1 = (row.part2==="01")? row.textContent.split(" ")[0] : null;
                row.bet_handicap2 = (row.part2==="01")? row.textContent.split(" ")[1] : null;

                row.bet_over1 = (row.part2==="03" && row.textContent.split(" ")[0]==="O")? row.textContent.split(" ")[1]  : null;
                row.bet_over2 = (row.part2==="03" && row.textContent.split(" ")[0]==="O")? row.textContent.split(" ")[2]  : null;

                row.bet_under1 = (row.part2==="03" && row.textContent.split(" ")[0]==="U")? row.textContent.split(" ")[1]  : null;
                row.bet_under2 = (row.part2==="03" && row.textContent.split(" ")[0]==="U")? row.textContent.split(" ")[2]  : null;

                //console.log(row);
            //}
            //catch(e){

            //}
            }

            return row;

        });
    }, msg.payload.value.results_selector);

    msg.topic = msg.topic + "_extracted";

    msg.payload = await rows.reduce((all, one) =>{

        if(!Array.isArray(all)){
            all = [];
        }

        result = all[one.event_number];

        console.log(result);

        if (result === undefined) {
            // not found
            one.all = [];
            all.splice(one.event_number, 0, one);
        }  else {
            // multiple items found
            result.all.splice(one.event_number, 0, one);
        }

        return all;
    });

    node.send(msg);
    //console.log(rows.join('\n'));
    //console.log(rows);

    browser.close();
})();
